package com.spring.api.service.config;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.context.properties.EnableConfigurationProperties;import org.springframework.context.annotation.Configuration;@Configuration@EnableConfigurationProperties(value = ReportApiProperties.class)public class ReportApiConfig {    @Autowired    private ReportApiProperties properties;    public static String token;    public String getLoginPath(){        return properties.getHost()+properties.getPaths().getLogin();    }    public String getReportsPath(){        return properties.getHost()+properties.getPaths().getReports();    }    public String getListPath(){        return properties.getHost()+properties.getPaths().getList();    }    public String getTransactionPath(){        return properties.getHost()+properties.getPaths().getTransaction();    }    public String getClientPath(){        return properties.getHost()+properties.getPaths().getClient();    }    public String getMerchantPath(){        return properties.getHost()+properties.getPaths().getMerchant();    }}