package com.spring.api.service.config;import org.springframework.boot.context.properties.ConfigurationProperties;@ConfigurationProperties(ignoreUnknownFields = false, prefix = "report")public class ReportApiProperties {    private String host;    private Paths paths;    public String getHost() {        return host;    }    public void setHost(String host) {        this.host = host;    }    public Paths getPaths() {        return paths;    }    public void setPaths(Paths paths) {        this.paths = paths;    }    public static class Paths{        private String login;        private String reports;        private String list;        private String transaction;        private String client;        private String merchant;        public String getLogin() {            return login;        }        public void setLogin(String login) {            this.login = login;        }        public String getReports() {            return reports;        }        public void setReports(String reports) {            this.reports = reports;        }        public String getList() {            return list;        }        public void setList(String list) {            this.list = list;        }        public String getTransaction() {            return transaction;        }        public void setTransaction(String transaction) {            this.transaction = transaction;        }        public String getClient() {            return client;        }        public void setClient(String client) {            this.client = client;        }        public String getMerchant() {            return merchant;        }        public void setMerchant(String merchant) {            this.merchant = merchant;        }    }}