package com.spring.api.service.domain;import java.util.Collections;import java.util.List;public class Reports {    private String status;    private List<Response> response;    public Reports() {        response= Collections.emptyList();    }    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }    public List<Response> getResponse() {        return response;    }    public void setResponse(List<Response> response) {        this.response = response;    }    public static class Response    {        private int count;        private int total;        private String currency;        public int getCount() {            return count;        }        public void setCount(int count) {            this.count = count;        }        public int getTotal() {            return total;        }        public void setTotal(int total) {            this.total = total;        }        public String getCurrency() {            return currency;        }        public void setCurrency(String currency) {            this.currency = currency;        }    }}